/*
 Write a function that groups an array of persons by first letter of first name and returns the groups as a JavaScript Object.
 Use Array#reduce.
 Use only array methods and no regular loops (for, while).
 */

// array of people generated by the first problem:

var peopleArray = [{
    firstname: 'Ivan',
    lastname: 'Ivanov',
    age: 34,
    isFemale: false
},
    {
        firstname: 'Maya',
        lastname: 'Stoyanova',
        age: 46,
        isFemale: true
    },
    {
        firstname: 'Peter',
        lastname: 'Petrov',
        age: 17,
        isFemale: false
    },
    {firstname: 'John', lastname: 'Brown', age: 39, isFemale: false},
    {
        firstname: 'Raya',
        lastname: 'Emilova',
        age: 16,
        isFemale: true
    },
    {
        firstname: 'Emilia',
        lastname: 'Zravkova',
        age: 30,
        isFemale: true
    },
    {
        firstname: 'Ana',
        lastname: 'Angelova',
        age: 24,
        isFemale: true
    },
    {
        firstname: 'Angel',
        lastname: 'Pashunov',
        age: 27,
        isFemale: false
    },
    {
        firstname: 'Ivaylo',
        lastname: 'Iliev',
        age: 33,
        isFemale: false
    },
    {
        firstname: 'Maria',
        lastname: 'Slavkova',
        age: 22,
        isFemale: true
    }];

var result = {},
    firstLetter,
    alphabetStr = 'absdefghijklmnopqrstuvwxyz',
    alpha = alphabetStr.split('');


alpha.forEach(function (letter) {
    peopleArray.reduce(function (arr, person) {
        firstLetter = person.firstname[0].toLowerCase();
        if (letter === firstLetter) {
            result[letter] = arr;
            arr.push(person);
        }
        return arr;
    }, []);
});

console.log('result =', result);
